import{_ as e,c as a,o as s,ah as l}from"./chunks/framework.BedXB533.js";const g=JSON.parse('{"title":"Student Guide","description":"","frontmatter":{},"headers":[],"relativePath":"user-guide/student.md","filePath":"user-guide/student.md"}'),t={name:"user-guide/student.md"};function n(r,i,o,u,c,d){return s(),a("div",null,[...i[0]||(i[0]=[l('<h1 id="student-guide" tabindex="-1">Student Guide <a class="header-anchor" href="#student-guide" aria-label="Permalink to “Student Guide”">​</a></h1><p>This guide covers all the features and functionality available to students in the Learning Management System.</p><h2 id="dashboard" tabindex="-1">Dashboard <a class="header-anchor" href="#dashboard" aria-label="Permalink to “Dashboard”">​</a></h2><p>When you log in as a student, you&#39;ll see your personalized dashboard showing:</p><ul><li>Your enrolled courses</li><li>Upcoming assignments and deadlines</li><li>Recent announcements</li><li>Your calendar with scheduled events</li></ul><h2 id="courses" tabindex="-1">Courses <a class="header-anchor" href="#courses" aria-label="Permalink to “Courses”">​</a></h2><h3 id="viewing-your-courses" tabindex="-1">Viewing Your Courses <a class="header-anchor" href="#viewing-your-courses" aria-label="Permalink to “Viewing Your Courses”">​</a></h3><p>Your courses are displayed on the dashboard. Each course card shows:</p><ul><li>Course name and code</li><li>Instructor information</li><li>Recent activity</li><li>Progress indicators</li></ul><h3 id="accessing-course-materials" tabindex="-1">Accessing Course Materials <a class="header-anchor" href="#accessing-course-materials" aria-label="Permalink to “Accessing Course Materials”">​</a></h3><p>To access a course:</p><ol><li>Click on the course card from your dashboard</li><li>Navigate through the course modules in the sidebar</li><li>Click on any module to view its contents</li></ol><p>Course materials may include:</p><ul><li>Lecture notes and slides</li><li>Video content</li><li>Reading materials (PDFs, documents)</li><li>External links and resources</li></ul><h3 id="course-files" tabindex="-1">Course Files <a class="header-anchor" href="#course-files" aria-label="Permalink to “Course Files”">​</a></h3><p>You can download course files provided by your instructor:</p><ol><li>Navigate to the Files section in your course</li><li>Browse or search for files</li><li>Click the download button to save files to your device</li></ol><h2 id="assignments" tabindex="-1">Assignments <a class="header-anchor" href="#assignments" aria-label="Permalink to “Assignments”">​</a></h2><h3 id="viewing-assignments" tabindex="-1">Viewing Assignments <a class="header-anchor" href="#viewing-assignments" aria-label="Permalink to “Viewing Assignments”">​</a></h3><p>Assignments are listed in:</p><ul><li>The Assignments section of each course</li><li>Your calendar</li><li>Your dashboard (for upcoming deadlines)</li></ul><h3 id="submitting-assignments" tabindex="-1">Submitting Assignments <a class="header-anchor" href="#submitting-assignments" aria-label="Permalink to “Submitting Assignments”">​</a></h3><p>To submit an assignment:</p><ol><li>Navigate to the assignment</li><li>Read the assignment instructions carefully</li><li>Upload your submission file(s)</li><li>Click &quot;Submit&quot; to finalize your submission</li></ol><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p>You can save drafts before submitting. Make sure to click &quot;Submit&quot; when you&#39;re ready for grading!</p></div><h3 id="checking-grades" tabindex="-1">Checking Grades <a class="header-anchor" href="#checking-grades" aria-label="Permalink to “Checking Grades”">​</a></h3><p>Once graded, you can view:</p><ul><li>Your score</li><li>Instructor feedback</li><li>Grade breakdown (if applicable)</li></ul><h2 id="quizzes" tabindex="-1">Quizzes <a class="header-anchor" href="#quizzes" aria-label="Permalink to “Quizzes”">​</a></h2><h3 id="taking-quizzes" tabindex="-1">Taking Quizzes <a class="header-anchor" href="#taking-quizzes" aria-label="Permalink to “Taking Quizzes”">​</a></h3><p>To take a quiz:</p><ol><li>Navigate to the quiz from your course</li><li>Read the instructions and time limits</li><li>Answer all questions</li><li>Review your answers before submitting</li><li>Click &quot;Submit Quiz&quot; when complete</li></ol><div class="warning custom-block"><p class="custom-block-title custom-block-title-default">WARNING</p><p>Pay attention to time limits! Quizzes may be timed, and you&#39;ll be automatically submitted when time expires.</p></div><h3 id="quiz-types" tabindex="-1">Quiz Types <a class="header-anchor" href="#quiz-types" aria-label="Permalink to “Quiz Types”">​</a></h3><p>The LMS supports various question types:</p><ul><li>Multiple choice</li><li>True/False</li><li>Short answer</li><li>Essay questions</li><li>Fill in the blank</li></ul><h3 id="viewing-quiz-results" tabindex="-1">Viewing Quiz Results <a class="header-anchor" href="#viewing-quiz-results" aria-label="Permalink to “Viewing Quiz Results”">​</a></h3><p>After submission:</p><ul><li>Some quizzes show immediate results</li><li>Others require instructor grading</li><li>Check your grades section for quiz scores and feedback</li></ul><h2 id="discussions" tabindex="-1">Discussions <a class="header-anchor" href="#discussions" aria-label="Permalink to “Discussions”">​</a></h2><h3 id="participating-in-discussions" tabindex="-1">Participating in Discussions <a class="header-anchor" href="#participating-in-discussions" aria-label="Permalink to “Participating in Discussions”">​</a></h3><p>To participate in course discussions:</p><ol><li>Navigate to the Discussions section</li><li>Browse existing discussion threads</li><li>Click &quot;New Discussion&quot; to start a new topic</li><li>Reply to existing discussions to join the conversation</li></ol><h3 id="discussion-etiquette" tabindex="-1">Discussion Etiquette <a class="header-anchor" href="#discussion-etiquette" aria-label="Permalink to “Discussion Etiquette”">​</a></h3><ul><li>Be respectful and professional</li><li>Stay on topic</li><li>Contribute meaningfully to discussions</li><li>Follow your instructor&#39;s guidelines</li></ul><h2 id="ai-chat-assistant" tabindex="-1">AI Chat Assistant <a class="header-anchor" href="#ai-chat-assistant" aria-label="Permalink to “AI Chat Assistant”">​</a></h2><p>The LMS includes an AI-powered chat assistant to help you:</p><ul><li>Understand course concepts</li><li>Get study tips and guidance</li><li>Find resources within your courses</li><li>Answer questions about course materials</li></ul><div class="info custom-block"><p class="custom-block-title custom-block-title-default">INFO</p><p>The AI assistant is a support tool. Always verify important information with your instructor.</p></div><h2 id="calendar" tabindex="-1">Calendar <a class="header-anchor" href="#calendar" aria-label="Permalink to “Calendar”">​</a></h2><h3 id="using-the-calendar" tabindex="-1">Using the Calendar <a class="header-anchor" href="#using-the-calendar" aria-label="Permalink to “Using the Calendar”">​</a></h3><p>Your calendar displays:</p><ul><li>Assignment due dates</li><li>Quiz availability windows</li><li>Course events</li><li>Personal reminders</li></ul><p>You can:</p><ul><li>View by day, week, or month</li><li>Filter events by course</li><li>Add personal events</li><li>Set reminders for important dates</li></ul><h2 id="progress-tracking" tabindex="-1">Progress Tracking <a class="header-anchor" href="#progress-tracking" aria-label="Permalink to “Progress Tracking”">​</a></h2><h3 id="viewing-your-progress" tabindex="-1">Viewing Your Progress <a class="header-anchor" href="#viewing-your-progress" aria-label="Permalink to “Viewing Your Progress”">​</a></h3><p>Track your progress in each course:</p><ul><li>Completion percentage for modules</li><li>Grades on assignments and quizzes</li><li>Overall course grade</li><li>Time spent on course activities</li></ul><h3 id="analytics" tabindex="-1">Analytics <a class="header-anchor" href="#analytics" aria-label="Permalink to “Analytics”">​</a></h3><p>View detailed analytics about your learning:</p><ul><li>Activity over time</li><li>Content engagement metrics</li><li>Comparison with course averages (if enabled)</li><li>Areas needing attention</li></ul><h2 id="profile-and-settings" tabindex="-1">Profile and Settings <a class="header-anchor" href="#profile-and-settings" aria-label="Permalink to “Profile and Settings”">​</a></h2><h3 id="managing-your-profile" tabindex="-1">Managing Your Profile <a class="header-anchor" href="#managing-your-profile" aria-label="Permalink to “Managing Your Profile”">​</a></h3><p>Update your profile information:</p><ol><li>Click on your profile icon</li><li>Select &quot;Profile Settings&quot;</li><li>Update your information</li><li>Save changes</li></ol><p>You can update:</p><ul><li>Display name</li><li>Profile picture</li><li>Contact information</li><li>Notification preferences</li></ul><h3 id="notification-settings" tabindex="-1">Notification Settings <a class="header-anchor" href="#notification-settings" aria-label="Permalink to “Notification Settings”">​</a></h3><p>Customize how you receive notifications:</p><ul><li>Email notifications</li><li>In-app notifications</li><li>Frequency preferences</li><li>Types of notifications</li></ul><h2 id="tips-for-success" tabindex="-1">Tips for Success <a class="header-anchor" href="#tips-for-success" aria-label="Permalink to “Tips for Success”">​</a></h2><ul><li>Check your dashboard daily for new announcements</li><li>Review assignment deadlines regularly</li><li>Participate actively in discussions</li><li>Utilize the AI chat for quick help</li><li>Keep track of your progress</li><li>Submit assignments before the deadline</li><li>Ask your instructor if you need clarification</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to “Troubleshooting”">​</a></h2><h3 id="common-issues" tabindex="-1">Common Issues <a class="header-anchor" href="#common-issues" aria-label="Permalink to “Common Issues”">​</a></h3><p><strong>Can&#39;t access a course:</strong></p><ul><li>Verify you&#39;re enrolled in the course</li><li>Check with your instructor if enrollment is pending</li></ul><p><strong>File upload issues:</strong></p><ul><li>Check file size limits</li><li>Ensure file format is supported</li><li>Try a different browser</li></ul><p><strong>Quiz problems:</strong></p><ul><li>Ensure stable internet connection</li><li>Don&#39;t navigate away during a timed quiz</li><li>Contact your instructor if technical issues occur</li></ul><h2 id="getting-help" tabindex="-1">Getting Help <a class="header-anchor" href="#getting-help" aria-label="Permalink to “Getting Help”">​</a></h2><p>If you encounter issues:</p><ol><li>Check this documentation</li><li>Use the AI chat assistant</li><li>Contact your course instructor</li><li>Reach out to system administrators for technical issues</li></ol>',84)])])}const p=e(t,[["render",n]]);export{g as __pageData,p as default};
